import React, { useState, MouseEvent, useEffect } from 'react';

import { Row, Col } from 'antd';
import { Center } from "../hoc/Center";
import {useParams} from "react-router-dom";
import {socketio} from "../socket";
import {Game as GameType} from '../types'

const matrix = [
    [1, 2, 3],
    [4, 5, 6],
    [7, 8, 9]
]

interface INum{
    num: number;
    error: boolean
}

const Game = () => {

    const [currentGame, setCurr] = useState<GameType>({} as GameType)

    const [numHover, setNumHover] = useState<INum | null>(null)

    const { id } = useParams()

    useEffect(() =>{
        socketio.emit('detail_game', id, (game: GameType) => setCurr(game))
    }, [id])

    const hovering = (e: MouseEvent<HTMLDivElement>, id: number) => {
        if (!currentGame.start) return

        const target = e.target as HTMLDivElement
        const hoveredCell = +target.innerText

        if (hoveredCell % 2 === 0){
            setNumHover({
                num: id,
                error: true
            })
        }else{
            setNumHover({
                num: id,
                error: false
            })
        }

    }

    const resetNumHover = () => setNumHover(null)

    return (
        <>
            <Center>
                <br/>
                <br/>
                { currentGame?.creator?.name } <br/>
                { !currentGame?.start ? "Гра заблокована": "Можна грати" }
                <br/>
                <br/>
                {
                    matrix.map(e => (
                        <>
                            <Row>
                                {e.map(i =>(
                                    <Col
                                        span={8}
                                        onMouseEnter={(e) => hovering(e, i)}
                                        onMouseLeave={resetNumHover}
                                        className={
                                        !currentGame.start
                                            ? "game-disable"
                                            :
                                            numHover?.num === i
                                                ?
                                                numHover.error
                                                    ? 'cell-error'
                                                    :
                                                'cell-hover'
                                                :
                                                'cell'
                                        }
                                    >{i}</Col>
                                ))}
                            </Row>
                        </>
                    ))
                }
            </Center>
        </>
    );
}

export default Game;
