import React, { FC, useState } from 'react';
import { Button } from "antd";
import { socketio } from "../socket";
import { Game } from "../types";

interface IProps{
    addNewGame: (item: Game) => void
}

const NewGame: FC<IProps> = ({ addNewGame }) => {

    const [isModalVisible, setIsModalVisible] = useState(false);

    const showModal = () => {
        setIsModalVisible(true);
    };

    const handleCancel = () => {
        setIsModalVisible(false);
    };

    const handleOk = async () => {
        await socketio.emit('new_game', (res: Game) => addNewGame(res))
        setIsModalVisible(false);
    }

    return (
        <>
            <Button type="primary" size={'large'} className={'add-button'} onClick={handleOk}>
                Створити гру
            </Button>
        </>
    );
}

export default NewGame;
