import React, { useState, MouseEvent, useEffect, useRef } from 'react';

import { Row, Col } from 'antd';
import { Center } from "../hoc/Center";
import { useParams } from "react-router-dom";
import {socketio} from "../socket";
import {Game as GameType} from '../types'


const matrix = [
    [1, 2, 3],
    [4, 5, 6],
    [7, 8, 9]
]

interface INum{
    num: number,
    error: boolean
}

interface JoinToGameValues{
    username: string,
    game_id: string
}

const Game = () => {

    const [currentGame, setCurr] = useState<GameType>({} as GameType)

    const [numHover, setNumHover] = useState<INum | null>(null)

    const { id } = useParams()

    const ref = useRef<HTMLDivElement[]>([])

    useEffect(() =>{
        socketio.emit('detail_game', id, (game: GameType) => {
            setCurr(game)
            const user = game.players.find(x => x.name === localStorage.getItem('username')) ?? false

            if (!user){
                const data: JoinToGameValues = {
                    username: localStorage.getItem('username')!,
                    game_id: game.id
                }

                socketio.emit('join_to_game', data, (d: any) =>{
                    setCurr(d)
                })
            }
        })

        socketio.on('start_or_finish_game', (d: any) =>{
            setCurr({...currentGame, start: d})
        })

        socketio.on('set_user_data', (d: any) =>{
            console.log('user', d)
        })

        return () => {}

    }, [id])

    console.log('---', currentGame)

    const hovering = (e: MouseEvent<HTMLDivElement>, id: number) => {
        if (!currentGame.start) return

        const target = e.target as HTMLDivElement
        const hoveredCell = target.innerText

        if (hoveredCell !== ''){
            setNumHover({
                num: id,
                error: true
            })
        }else{
            setNumHover({
                num: id,
                error: false
            })
        }
    }

    const setFigure = (e: any) =>{
        // if (!currentGame.creator.allow_move || numHover?.error) return

        // console.log(e.target.id)
        // const d = {
        //     figure: currentGame.creator.figure,
        //     cell: e.target.id
        // }
        // socketio.emit('t', d)
        // e.target.innerText = currentGame.creator.figure
        // setNumHover({num: 44, error: true})
    }

    const resetNumHover = () => setNumHover(null)

    return (
        <>
            <Center>
                <br/>
                <br/>
                { currentGame?.players && currentGame?.players[0].name } <br/>
                { !currentGame?.start ? "Гра заблокована": "Можна грати" }
                <br/>
                <br/>
                {
                    matrix.map(e => (
                        <>
                            <Row>
                                {e.map(i =>(
                                    <Col
                                        ref={(element: any) => {
                                            ref.current[i] = element;
                                        }}
                                        id={String(i)}
                                        span={8}
                                        onMouseEnter={(e) => hovering(e, i)}
                                        onClick={setFigure}
                                        onMouseLeave={resetNumHover}
                                        className={
                                        !currentGame.start
                                            ? "game-disable"
                                            :
                                            numHover?.num === i
                                                ?
                                                numHover.error
                                                    ? 'cell-error'
                                                    :
                                                'cell-hover'
                                                :
                                                'cell'
                                        }
                                    ></Col>
                                ))}
                            </Row>
                        </>
                    ))
                }
            </Center>
        </>
    );
}

export default Game;
