import React, { FC, useState } from 'react';
import { Button, Modal } from "antd";
import { socketio } from "../socket";
import { Game } from "../types";

interface IProps{
    addNewGame: (item: Game) => void
}

const NewGame: FC<IProps> = ({ addNewGame }) => {

    const [isModalVisible, setIsModalVisible] = useState(false);
    const [figure, setFigure] = useState<'X' | 'O'>('X');

    const showModal = () => {
        setIsModalVisible(true);
    };

    const handleCancel = () => {
        setIsModalVisible(false);
    };

    const handleOk = async () => {
        await socketio.emit('new_game', figure, (res: Game) => {
            console.log(res)
            addNewGame(res)
        })
        setIsModalVisible(false);
    }

    return (
        <>
            <Button type="primary" size={'large'} className={'add-button'} onClick={showModal}>
                Створити гру
            </Button>
            <Modal title="Basic Modal" visible={isModalVisible} onOk={handleOk} onCancel={handleCancel}>
                <Button
                    type="primary"
                    size={'large'}
                    className={'add-button'}
                    onClick={() => setFigure('X')}
                    shape="circle"
                >
                    X
                </Button>
                <Button
                    type="primary"
                    size={'large'}
                    className={'add-button'}
                    onClick={() => setFigure('O')}
                    shape="circle"
                >
                    O
                </Button>
            </Modal>
        </>
    );
}

export default NewGame;
