import React, { useState, MouseEvent, useEffect } from 'react';

import { Row, Col } from 'antd';
import { Center } from "../hoc/Center";
import {useParams} from "react-router-dom";
import {socketio} from "../socket";

const matrix = [
    [1, 2, 3],
    [4, 5, 6],
    [7, 8, 9]
]

interface INum{
    num: number;
    error: boolean
}

function Game() {

    const [currentGame, useCurrentGame] = useState({})

    const { id } = useParams()

    useEffect(() =>{
        socketio.emit('')
    }, [id])

    const [numHover, setNumHover] = useState<INum | null>(null)

    const hovering = (e: MouseEvent<HTMLDivElement>, id: number) => {
        const target = e.target as HTMLDivElement
        const hoveredCell = +target.innerText

        if (hoveredCell % 2 === 0){
            setNumHover({
                num: id,
                error: true
            })
        }else{
            setNumHover({
                num: id,
                error: false
            })
        }

    }

    const resetNumHover = () => setNumHover(null)

    return (
        <>
            <Center>
                {
                    matrix.map(e => (
                        <>
                            <Row>
                                {e.map(i =>(
                                    <Col
                                        span={8}
                                        onMouseEnter={(e) => hovering(e, i)}
                                        onMouseLeave={resetNumHover}
                                        className={
                                            numHover?.num === i
                                                ?
                                                numHover.error
                                                    ? 'cell-error'
                                                    :
                                                'cell-hover'
                                                :
                                                'cell'
                                        }
                                    >{i}</Col>
                                ))}
                            </Row>
                        </>
                    ))
                }
            </Center>
        </>
    );
}

export default Game;
