import React, { useState, MouseEvent, useEffect, useRef } from 'react';

import { Row, Col } from 'antd';
import { Center } from "../hoc/Center";
import {useParams} from "react-router-dom";
import {socketio} from "../socket";
import {Game as GameType} from '../types'

const matrix = [
    [1, 2, 3],
    [4, 5, 6],
    [7, 8, 9]
]

interface INum{
    num: number,
    error: boolean
}

interface JoinToGameValues{
    username: string,
    game_id: string
}

const Game = () => {

    const [currentGame, setCurr] = useState<GameType>({} as GameType)

    const [numHover, setNumHover] = useState<INum | null>(null)

    const { id } = useParams()

    const ref = useRef<HTMLDivElement[]>([])

    useEffect(() =>{
        socketio.emit('detail_game', id, (game: GameType) => {
            setCurr(game)

            if (game.creator.name !== localStorage.getItem('username')){

                const data: JoinToGameValues = {
                    username: localStorage.getItem('username')!,
                    game_id: game.id
                }
                socketio.emit('join_to_game', data, (d: any) =>{
                    console.log(d)
                    setCurr(d)
                })
            }
        })

        return () =>{
            console.log('if any player left game, then game remove')
        }

    }, [id])

    const hovering = (e: MouseEvent<HTMLDivElement>, id: number) => {
        if (!currentGame.start) return

        const target = e.target as HTMLDivElement
        const hoveredCell = target.innerText

        if (hoveredCell !== ''){
            setNumHover({
                num: id,
                error: true
            })
        }else{
            setNumHover({
                num: id,
                error: false
            })
        }
    }

    const setFigure = (e: any) =>{
        if (!currentGame.creator.allow_move) return
        console.log(e.target.id)
        socketio.emit('t', e.target.id)
        e.target.innerText = currentGame.creator.figure
        setNumHover({num: 44, error: true})
    }

    const resetNumHover = () => setNumHover(null)

    return (
        <>
            <Center>
                <br/>
                <br/>
                { currentGame?.creator?.name } <br/>
                { !currentGame?.start ? "Гра заблокована": "Можна грати" }
                <br/>
                <br/>
                {
                    matrix.map(e => (
                        <>
                            <Row>
                                {e.map((i: number, idx: number) =>(
                                    <Col
                                        ref={(element: any) => {
                                            ref.current[i] = element;
                                        }}
                                        id={String(idx + 1)}
                                        span={8}
                                        onMouseEnter={(e) => hovering(e, i)}
                                        onClick={setFigure}
                                        onMouseLeave={resetNumHover}
                                        className={
                                        !currentGame.start
                                            ? "game-disable"
                                            :
                                            numHover?.num === i
                                                ?
                                                numHover.error
                                                    ? 'cell-error'
                                                    :
                                                'cell-hover'
                                                :
                                                'cell'
                                        }
                                    ></Col>
                                ))}
                            </Row>
                        </>
                    ))
                }
            </Center>
        </>
    );
}

export default Game;
